import React from "react";
import {
  Navbar,
  Card,
  Button,
  Badge,
  Dropdown,
  Row,
  Col,
} from "react-bootstrap";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import ApprovalHeader from "./ApprovalHeader";

const Approvals = () => {
  const data = [
    {
      empName: "1",
      descpt: "aaa",
      amt: "2424",
      approve: 1,
      reject: 2,
    },
  ];

  const columns = [
    {
      dataField: "empName",
      text: "Employee Name",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "descpt",
      text: "Description",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "descpt",
      text: "Amount",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "approve",
      text: "",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "reject",
      text: "",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
  ];

  const expandRow = {
    renderer: (row) => (
      <div>
        <p>{`This Expand row belongs to rowKey ${row.id}`}</p>
        <p>
          You can render anything here, also you can add additional data on
          every row object
        </p>
        <p>
          expandRow.renderer callback will pass the origin row object to you
        </p>
      </div>
    ),
    showExpandColumn: true,
  };
  const { SearchBar } = Search;
  const afterSearch = (newResult) => {
    console.log(newResult);
  };

  return (
    <>
      <ApprovalHeader />
      <Card style={{ width: "95%" }} className="ms-5">
        <Card.Header as="h4">
          <Col>
            <div className="dark">EXPENSE APPROVAL</div>
          </Col>
        </Card.Header>
        <Card.Body>
          <ToolkitProvider
            keyField="name"
            data={data}
            columns={columns}
            search={{ afterSearch }}
          >
            {(props) => (
              <div>
                <SearchBar id="searchbar" {...props.searchProps} />
                <br />
                <BootstrapTable
                  //   ref={ n => this.node = n }
                  keyField="id"
                  data={data}
                  columns={columns}
                  //   defaultSorted={defaultSorted}
                  headerClasses="header-class"
                  //   filter={ filterFactory() }
                  pagination={paginationFactory()}
                  expandRow={expandRow}
                  // selectRow={{ mode: "checkbox", clickToSelect: true }}

                  {...props.baseProps}
                />
              </div>
            )}
          </ToolkitProvider>
        </Card.Body>
      </Card>
    </>
  );
};

export default Approvals;
