import React from "react";
import Tabs from "react-bootstrap/Tabs";
import Tab from "react-bootstrap/Tab";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import FloatingLabel from "react-bootstrap/FloatingLabel";
import { Dropdown } from "react-bootstrap";
import Modal from "react-bootstrap/Modal";
import FileUpload from "../FileUpload";
import { Formik } from "formik";
import * as yup from "yup";
import { useDispatch } from "react-redux";
import { useEffect } from "react";
import { getClients } from "../../actions/client";
import { getEmployees } from "../../actions/employees";
import { useSelector } from "react-redux";
import { useState } from "react";
import { FormControl } from "react-bootstrap";

const schema = yup.object().shape({
  projectName: yup.string().required(),
  projectDescription: yup.string(),
  duration: yup.number().required(),
  cost: yup.number().required(),
});

const AddProject = (props) => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getClients())
      .then(() => {
        console.log("Clients fetched successfully");
      })
      .catch(() => {
        console.log("Unable to fetch clients");
      });
  }, [dispatch]);

  useEffect(() => {
    dispatch(getEmployees())
      .then(() => {
        console.log("Employees fetched successfully");
      })
      .catch(() => {
        console.log("Unable to fetch employees");
      });
  }, [dispatch]);

  const { employees } = useSelector((state) => state.employees);
  const { clients } = useSelector((state) => state.client);
  const membersAdded = [];
  const { user: currentUser } = useSelector((state) => state.auth);
  membersAdded.push(currentUser);
  console.log(clients);
  console.log(employees);

  const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
    <a
      href=""
      ref={ref}
      onClick={(e) => {
        e.preventDefault();
        onClick(e);
      }}
    >
      {children}
      &#x25bc;
    </a>
  ));

  // forwardRef again here!
  // Dropdown needs access to the DOM of the Menu to measure it
  const CustomMenu = React.forwardRef(
    ({ children, style, className, "aria-labelledby": labeledBy }, ref) => {
      const [value, setValue] = useState("");

      return (
        <div
          ref={ref}
          style={style}
          className={className}
          aria-labelledby={labeledBy}
        >
          <FormControl
            autoFocus
            className="mx-3 my-2 w-auto"
            placeholder="Type to filter..."
            onChange={(e) => setValue(e.target.value)}
            value={value}
          />
          <ul className="list-unstyled">
            {React.Children.toArray(children).filter(
              (child) =>
                !value || child.props.children.toLowerCase().startsWith(value)
            )}
          </ul>
        </div>
      );
    }
  );

  return (
    <Formik
      validationSchema={schema}
      onSubmit={console.log}
      initialValues={{
        projectName: "",
        projectDescription: "",
        duration: 0.0,
        cost: 0.0,
        file: null,
      }}
    >
      {({
        handleSubmit,
        handleChange,
        handleBlur,
        values,
        touched,
        isValid,
        errors,
      }) => (
        <Form noValidate onSubmit={handleSubmit}>
          <Modal {...props} size="lg" backdrop="static">
            <Modal.Header closeButton>
              <Modal.Title>Add Project</Modal.Title>
            </Modal.Header>

            <Modal.Body>
              <Row>
                <Form.Group as={Col} controlId="projectName">
                  <Form.Label>Project Name</Form.Label>
                  <Form.Control
                    name="projectName"
                    value={values.projectName}
                    onChange={handleChange}
                    isInvalid={!!errors.projectName}
                  />
                  <Form.Control.Feedback type="invalid">
                    Project Name is a required field!
                  </Form.Control.Feedback>
                </Form.Group>
              </Row>
              <br />
              <Tabs defaultActiveKey="basic">
                <Tab eventKey="basic" title="Basic">
                  <br />
                  <Row>
                    <Form.Group as={Col} controlId="clientName">
                      <Form.Label>Client Name</Form.Label>
                      <Form.Select defaultValue="Choose...">
                        {/* <option>Client1</option>
                        <option>Client2</option> */}
                        {clients.map((client) => {
                          return (
                            <option>
                              {client.fullName +
                                " (Client ID: " +
                                client.id +
                                ")"}
                            </option>
                          );
                        })}
                      </Form.Select>
                    </Form.Group>
                    {/* <Form.Group as={Col} controlId="projectCode">
                      <Form.Label>Project Code</Form.Label>
                      <Form.Control />
                    </Form.Group> */}
                  </Row>
                  <Row>
                    <Form.Group as={Col} controlId="teamLead">
                      <Form.Label>Team Lead</Form.Label>
                      <Form.Select defaultValue="Choose...">
                        <option>TL1</option>
                        <option>TL2</option>
                      </Form.Select>
                    </Form.Group>
                    <Form.Group as={Col} controlId="projectManager">
                      <Form.Label>Project Manager</Form.Label>
                      <Form.Select defaultValue="Choose...">
                        <option>PM1</option>
                        <option>PM2</option>
                      </Form.Select>
                    </Form.Group>
                  </Row>
                </Tab>

                <Tab eventKey="team" title="Team">
                  <br />
                  <Form.Group as={Col} className="float-end" id="selectAllTeam">
                    <Form.Check inline type="checkbox" label=" Select All" />
                  </Form.Group>

                  <Row>
                    <Form.Group as={Col} controlId="teamMembers">
                      <Form.Label>
                        <strong>Team Members: {membersAdded.length}</strong>
                      </Form.Label>
                      <ul>
                        {membersAdded.map((m) => {
                          return <li>{m.firstName + " " + m.lastName}</li>;
                        })}
                      </ul>
                    </Form.Group>
                  </Row>
                  <Row>
                    <Form.Group controlId="teamMembers">
                      {/* <Form.Control placeholder="Search for Team Members" /> */}
                      <Dropdown>
                        <Dropdown.Toggle
                          as={CustomToggle}
                          id="dropdown-custom-components"
                        >
                          Search Filter
                        </Dropdown.Toggle>

                        <Dropdown.Menu as={CustomMenu}>
                          {membersAdded
                          .map((m) => {
                            return(<Dropdown.Item eventKey={m.id}>{m.firstName + " " + m.lastName + " (Employee ID: " + m.id + ")"}</Dropdown.Item>)
                          })}

                        </Dropdown.Menu>
                      </Dropdown>
                    </Form.Group>
                  </Row>
                </Tab>

                <Tab eventKey="approvals" title="Approvals">
                  <br />
                  <Row>
                    <Form.Group as={Col} controlId="timeSheetApproval">
                      <Form.Label>Timesheet Approval Type</Form.Label>
                      <Form.Select defaultValue="Choose...">
                        <option>Team Lead</option>
                        {/* <option>Project Manager</option> */}
                      </Form.Select>
                    </Form.Group>
                    <Form.Group as={Col} controlId="expenseApproval">
                      <Form.Label>Expense Approval Type</Form.Label>
                      <Form.Select defaultValue="Choose...">
                        <option>Project Manager</option>
                        {/* <option>Team Lead</option> */}
                      </Form.Select>
                    </Form.Group>
                  </Row>
                </Tab>

                <Tab eventKey="advanced" title="Advanced">
                  <br />
                  <Form.Group controlId="projDescription">
                    <Form.Label>Project Description</Form.Label>
                    <FloatingLabel controlId="projDescriptionText">
                      <Form.Control
                        as="textarea"
                        placeholder="Leave a comment here"
                        style={{ height: "100px" }}
                        name="projectDescription"
                        value={values.projectDescription}
                        onChange={handleChange}
                        isInvalid={!!errors.projectDescription}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.projectDescription}
                      </Form.Control.Feedback>
                    </FloatingLabel>
                  </Form.Group>
                  <Row>
                    <Form.Group as={Col} controlId="duration">
                      <Form.Label>Duration (Hours)</Form.Label>
                      <Form.Control
                        name="duration"
                        value={values.duration}
                        onChange={handleChange}
                        isInvalid={!!errors.duration}
                      />
                      <Form.Control.Feedback type="invalid">
                        Duration is required, and must be a number!
                      </Form.Control.Feedback>
                    </Form.Group>
                    <Form.Group as={Col} controlId="estimatedCost">
                      <Form.Label>Project Estimated Cost</Form.Label>
                      <Form.Control
                        name="cost"
                        value={values.cost}
                        onChange={handleChange}
                        isInvalid={!!errors.cost}
                      />
                      <Form.Control.Feedback type="invalid">
                        Cost is required, and must be a number!
                      </Form.Control.Feedback>
                    </Form.Group>
                  </Row>
                  <Row>
                    <Form.Group as={Col} controlId="projectStatus">
                      <Form.Label>Project Status</Form.Label>
                      <Form.Select defaultValue="Choose...">
                        <option>To-Do</option>
                        <option>In progress</option>
                        <option>Suspended</option>
                        <option>Completed</option>
                      </Form.Select>
                    </Form.Group>
                    {/* <Form.Group as={Col} controlId="projectType">
                      <Form.Label>Project Type</Form.Label>
                      <Form.Select defaultValue="Choose...">
                        <option>Technology</option>
                        <option>HR</option>
                      </Form.Select>
                    </Form.Group> */}
                  </Row>
                </Tab>
                <Tab eventKey="dates" title="Dates">
                  <br />
                  <Row>
                    <Form.Group as={Col} controlId="startDate">
                      <Form.Label>Start Date</Form.Label>
                      <Form.Control type="date" />
                    </Form.Group>
                    <Form.Group as={Col} controlId="dueDate">
                      <Form.Label>Due Date</Form.Label>
                      <Form.Control type="date" />
                    </Form.Group>
                  </Row>
                  {/* <Form.Group as={Col} controlId="projectTemplate">
                    <Form.Label>Project Template</Form.Label>
                    <Form.Select defaultValue="Select">
                      <option>Template1</option>
                      <option>Template2</option>
                    </Form.Select>
                  </Form.Group> */}
                </Tab>
                <Tab eventKey="attachment" title="Attachment">
                  <br />
                  <FileUpload />
                  {/* <FileUpload name="demo[]" url="./upload" /> */}
                  {/* https://www.primefaces.org/primereact/showcase/#/fileupload */}
                </Tab>
              </Tabs>
            </Modal.Body>

            <Modal.Footer>
              <Button variant="success" type="submit">
                Add Project
              </Button>
              <Button variant="secondary" onClick={props.onHide}>
                Close
              </Button>
            </Modal.Footer>
          </Modal>
        </Form>
      )}
    </Formik>
  );
};

export default AddProject;
