import React from "react";
import {
  Container,
  Card,
  Dropdown,
  Button,
  Tab,
  Tabs,
  ListGroup,
  CardGroup,
  Row,
  Col,
} from "react-bootstrap";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import FileUpload from "../FileUpload";

const ProjectSummaryDashboard = () => {
  const SortCaret = (order, column) => {
    if (!order)
      return (
        <span class="float-end">
          <font color="grey">
            &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "asc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
          <font color="grey">
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "desc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;
          <font color="grey">
            <i class="bi bi-caret-up-fill"></i>
          </font>
          <i class="bi bi-caret-down-fill"></i>
        </span>
      );
    return null;
  };

  const columns = [
    {
      dataField: "UF1",
      text: "",
      sort: true,
      sortCaret: SortCaret,
      headerStyle: (colum, colIndex) => {
        return { width: "60px", textAlign: "center" };
      },
    },
    {
      dataField: "UF2",
      text: "",
      sort: true,
      sortCaret: SortCaret,
      headerStyle: (colum, colIndex) => {
        return { width: "60px", textAlign: "center" };
      },
    },
    {
      dataField: "pName",
      text: "Name",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "pStatus",
      text: "Status",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectPriority",
      text: "Priority",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectHours",
      text: "Hours",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "UF3",
      text: "",
    },
  ];

  const projectData = [];

  const defaultSorted = [
    {
      dataField: "UF1",
      order: "asc", //desc
    },
  ];

  const customTotal = (from, to, size) => (
    <span className="react-bootstrap-table-pagination-total">
      &nbsp; Showing {from} to {to} of {size} Results
    </span>
  );

  const options = {
    // paginationSize: 4,
    // pageStartIndex: 0,
    // alwaysShowAllBtns: true, // Always show next and previous button
    // withFirstAndLast: false, // Hide the going to First and Last page button
    // hideSizePerPage: true, // Hide the sizePerPage dropdown always
    // hidePageListOnlyOnePage: true, // Hide the pagination list when only one page
    // firstPageText: 'First',
    // prePageText: 'Back',
    // nextPageText: 'Next',
    // lastPageText: 'Last',
    // nextPageTitle: 'First page',
    // prePageTitle: 'Pre page',
    // firstPageTitle: 'Next page',
    // lastPageTitle: 'Last page',
    showTotal: true,
    paginationTotalRenderer: customTotal,
    disablePageTitle: true,
    sizePerPageList: [
      {
        text: "5",
        value: 5,
      },
      {
        text: "10",
        value: 10,
      },
      {
        text: "All",
        value: projectData.length,
      },
    ],
  };

  const { SearchBar } = Search;

  return (
    // <Container>
    <Row xs={1} md={2} className="g-4">
        <Col xs={12}>
      {/* <CardGroup style={{ width: '150rem' }}> */}
        <Card>
          <Card.Header as="h4">
            MARSVIEW.AI
            <Dropdown className="float-end">
              <Dropdown.Toggle id="options-menu" variant="secondary">
                <i class="bi bi-gear-fill"></i> Options
              </Dropdown.Toggle>

              <Dropdown.Menu variant="dark">
                <Dropdown.Item href="">View</Dropdown.Item>
                <Dropdown.Item href="">Edit</Dropdown.Item>
                <Dropdown.Item href="">Delete</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
            <Card.Text className="float-end">&nbsp;</Card.Text>
            <Dropdown className="float-end">
              <Dropdown.Toggle id="options-menu" variant="success">
                + Add
              </Dropdown.Toggle>

              <Dropdown.Menu>
                <Dropdown.Item href="">View</Dropdown.Item>
                <Dropdown.Item href="">Edit</Dropdown.Item>
                <Dropdown.Item href="">Delete</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
            <Card.Text className="float-end">&nbsp;</Card.Text>
            <Button
              variant="danger"
              className="float-end"
              id="deleteProject"
              disabled={true}
            >
              Delete Selected
            </Button>
            <br />
            <Card.Text>
              <font color="blue">
                <h1>Speech Analytics</h1>
              </font>
            </Card.Text>
          </Card.Header>
          <Card.Body>
            <Tabs defaultActiveKey="task">
              <Tab eventKey="task" title="Task">
                <br />
                <ToolkitProvider
                  keyField="projectCode"
                  data={projectData}
                  columns={columns}
                  search
                >
                  {(props) => (
                    <div>
                      <SearchBar
                        {...props.searchProps}
                        srText="Search Projects :"
                        placeholder="Enter text"
                      />
                      <br />
                      <BootstrapTable
                        {...props.baseProps}
                        hover
                        selectRow={{ mode: "checkbox" }}
                        defaultSorted={defaultSorted}
                        pagination={paginationFactory(options)}
                        noDataIndication="No data available in table"
                      />
                    </div>
                  )}
                </ToolkitProvider>
              </Tab>

              <Tab eventKey="milestone" title="Milestone">
                <br />
              </Tab>

              <Tab eventKey="team" title="Team">
                <br />
              </Tab>

              <Tab eventKey="attachment" title="Attachment">
                <br />
                <FileUpload />
              </Tab>
            </Tabs>
          </Card.Body>
        </Card>
        </Col>
        <Col>
        <Card>
          <Card.Header>
            <Card.Text>
              <font color="blue">
                <h3>PROJECT SUMMARY</h3>
              </font>
            </Card.Text>
          </Card.Header>
          <Card.Body>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Card.Text>TOTAL HOURS</Card.Text>
              </ListGroup.Item>

              <ListGroup.Item>
                <Card.Text>TOTAL EXPENSES</Card.Text>
              </ListGroup.Item>
            </ListGroup>
          </Card.Body>
        </Card>
        </Col>
      {/* </CardGroup> */}
    {/* </Container> */}
    </Row>
  );
};

export default ProjectSummaryDashboard;
