import React from "react";
import {
  Navbar,
  Card,
  Button,
  Badge,
  Dropdown,
  Row,
  Col,
} from "react-bootstrap";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import ApprovalHeader from "./ApprovalHeader";
import { useDispatch } from "react-redux";
import { useEffect } from "react";
import { useSelector } from "react-redux";
import { getSubmittedExpenses } from "../../actions/expense.actions";

const Approvals = () => {

  const dispatch = useDispatch();
  useEffect(() => {
      dispatch(getSubmittedExpenses())
      .then(() => {
        console.log("GOT");
      })
      .catch(() => {
        // openToast();
        console.log("NOT GOT");
      });
  },[dispatch])

  const viewButton = (id) => {
    return (
      <Button variant="secondary" id={id}>
        View Details
      </Button>
    )
  }

  const SortCaret = (order, column) => {
    if (!order)
      return (
        <span className="float-end">
          <font color="grey">
            &nbsp;&nbsp;<i className="bi bi-caret-up-fill"></i>
            <i className="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "asc")
      return (
        <span className="float-end">
          &nbsp;&nbsp;<i className="bi bi-caret-up-fill"></i>
          <font color="grey">
            <i className="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "desc")
      return (
        <span className="float-end">
          &nbsp;&nbsp;
          <font color="grey">
            <i className="bi bi-caret-up-fill"></i>
          </font>
          <i className="bi bi-caret-down-fill"></i>
        </span>
      );
    return null;
  };

  const onApprove = (e) => {
    const payload = {
      "status":"APPROVED"
    }
    console.log("APPROVED");
    // dispatch(updateExpenseState(e.target.id, payload))
    //   .then(() => {
    //     dispatch(getSubmittedExpenses())
    //       .then(() => {
    //         console.log("Approved, and expenses fetched successfully");
    //       })
    //       .catch(() => {
    //         console.log("Unable to fetch expenses");
    //       });
    //   })
    //   .catch(() => {
    //     // openToast();
    //     console.log("OK");
    //   });
  };

  const onReject = (e) => {
    const payload = {
      "status":"REJECTED"
    }
    console.log("REJECTED");
  //   dispatch(updateExpenseState(e.target.id, payload))
  //     .then(() => {
  //       dispatch(getSubmittedExpenses())
  //         .then(() => {
  //           console.log("Rejected, and expenses fetched successfully");
  //         })
  //         .catch(() => {
  //           console.log("Unable to fetch expenses");
  //         });
  //     })
  //     .catch(() => {
  //       // openToast();
  //       console.log("OK");
  //     });
  };

  // const {expensesSubmitted:content} = useSelector(state => state.expensesSubmitted);

  // console.log(content);

  const approveButton = (id) => {
    return (
      <Button variant="success" onClick={onApprove} id={id}>
        Approve
      </Button>
    )
  }

  const rejectButton = (id) => {
    return (
      <Button variant="danger" onClick={onReject} id={id}>
        Reject
      </Button>
    )
  }

  const data = [
    {
      empName: "1",
      descpt: "aaa",
      amt: "2424",
      viewDetails: <center>{viewButton(1)}</center>,
      approve: <center>{approveButton(1)}</center>,
      reject: <center>{rejectButton(1)}</center>,
    },
  ];

  const columns = [
    {
      dataField: "empName",
      text: "Employee Name",
      sort: true,
      sortCaret: SortCaret,

      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "descpt",
      text: "Description",
      sort: true,
      sortCaret: SortCaret,
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "amt",
      text: "Amount",
      sort: true,
      sortCaret: SortCaret,
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "viewDetails",
      text: "",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "approve",
      text: "",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "reject",
      text: "",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
  ];

  const expandRow = {
    renderer: (row) => (
      <div>
        <p>{`This Expand row belongs to rowKey ${row.id}`}</p>
        <p>
          You can render anything here, also you can add additional data on
          every row object
        </p>
        <p>
          expandRow.renderer callback will pass the origin row object to you
        </p>
      </div>
    ),
    showExpandColumn: true,
  };

  const { SearchBar } = Search;

  return (
    <>
      <ApprovalHeader />
      <Card style={{ width: "95%" }} className="ms-5">
        <Card.Header as="h4">
          <Col>
            <div className="dark">EXPENSE APPROVAL</div>
          </Col>
        </Card.Header>
        <Card.Body>
          <ToolkitProvider
            keyField="empName"
            data={data}
            columns={columns}
            search
          >
            {(props) => (
              <div>
                <SearchBar id="searchbar" {...props.searchProps} 
                srText="Search Expenses :"
                placeholder="Enter text"/>
                <br />
                <BootstrapTable
                  keyField="approve"
                  {...props.baseProps}
                  hover
                  // data={data}
                  // columns={columns}
                  //   defaultSorted={defaultSorted}
                  // headerClasses="header-class"
                  //   filter={ filterFactory() }
                  pagination={paginationFactory()}
                  // expandRow={expandRow}
                  noDataIndication="No data available in the table for you"

                />
              </div>
            )}
          </ToolkitProvider>
        </Card.Body>
      </Card>
    </>
  );
};

export default Approvals;
