import React from "react";
import { Container, Card, Dropdown, Button, Tab, Tabs } from "react-bootstrap";
import FileUpload from "../FileUpload";

const ProjectSummaryDashboard = () => {
  const SortCaret = (order, column) => {
    if (!order)
      return (
        <span class="float-end">
          <font color="grey">
            &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "asc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
          <font color="grey">
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "desc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;
          <font color="grey">
            <i class="bi bi-caret-up-fill"></i>
          </font>
          <i class="bi bi-caret-down-fill"></i>
        </span>
      );
    return null;
  };

  const columns = [
    {
      dataField: "UF1",
      text: "",
      sort: true,
      sortCaret: SortCaret,
      headerStyle: (colum, colIndex) => {
        return { width: "40px", textAlign: "center" };
      },
    },
    {
      dataField: "UF2",
      text: "",
      sort: true,
      sortCaret: SortCaret,
      headerStyle: (colum, colIndex) => {
        return { width: "40px", textAlign: "center" };
      },
    },
    {
      dataField: "pName",
      text: "Name",
      sort: true,
      sortCaret: SortCaret,

      headerStyle: (colum, colIndex) => {
        return { width: "100px", textAlign: "center" };
      },
    },
    {
      dataField: "pStatus",
      text: "Status",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectPriority",
      text: "Priority",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectHours",
      text: "Hours",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "UF3",
      text: "",
    },
  ];

  const defaultSorted = [
    {
      dataField: "UF1",
      order: "asc", //desc
    },
  ];

  return (
    <Container>
      <Card style={{ width: "100%" }}>
        <Card.Header as="h4">
          MARSVIEW.AI
          <Dropdown className="float-end">
            <Dropdown.Toggle id="options-menu" variant="secondary">
              <i class="bi bi-gear-fill"></i> Options
            </Dropdown.Toggle>

            <Dropdown.Menu variant="dark">
              <Dropdown.Item href="" active>
                View
              </Dropdown.Item>
              <Dropdown.Item href="">Edit</Dropdown.Item>
              <Dropdown.Item href="">Delete</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
          <Card.Text className="float-end">&nbsp;</Card.Text>
          <Dropdown className="float-end">
            <Dropdown.Toggle id="options-menu" variant="success">
              + Add
            </Dropdown.Toggle>

            <Dropdown.Menu variant="success">
              <Dropdown.Item href="" active>
                View
              </Dropdown.Item>
              <Dropdown.Item href="">Edit</Dropdown.Item>
              <Dropdown.Item href="">Delete</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
          <Card.Text className="float-end">&nbsp;</Card.Text>
          <Button
            variant="danger"
            className="float-end"
            id="deleteProject"
            disabled={true}
          >
            Delete Selected
          </Button>
          <br />
          <Card.Text>
            <font color="blue">
              <h1>Speech Analytics</h1>
            </font>
          </Card.Text>
        </Card.Header>
        <Card.Body>
          <Tabs defaultActiveKey="task">
            <Tab eventKey="task" title="Task">
              <br />
            </Tab>

            <Tab eventKey="milestone" title="Milestone">
              <br />
            </Tab>

            <Tab eventKey="team" title="Team">
              <br />
            </Tab>

            <Tab eventKey="attachment" title="Attachment">
              <br />
              <FileUpload />
            </Tab>
          </Tabs>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default ProjectSummaryDashboard;
