import { FETCH_PROJECTS, FETCH_PROJECTS_FAIL, ADD_PROJECTS, ADD_PROJECTS_FAIL } from "../actions/types";

const initialState = { projects: [] };

export default function (state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case FETCH_PROJECTS:
      return {
        ...state,
        projects: payload.projects,
      };
    case FETCH_PROJECTS_FAIL:
      return {
        ...state,
        projects: [],
      };
      case ADD_PROJECTS:
        const newProjects = [...state.projects];
        newProjects.push(payload.project);
        console.log("WHY NOT WORKING (IN REDUCER)??");
        console.log(newProjects);
        return {
          ...state,
          // projects: projects,
        };
      case ADD_PROJECTS_FAIL:
        return {
          ...state,
        };
    default:
      return state;
  }
}
