import React from "react";
import {
  Navbar,
  Card,
  Button,
  Badge,
  Dropdown,
  Row,
  Col,
} from "react-bootstrap";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";

const Approvals = () => {
  const data = [
    {
      id: "1",
      name: "aaa",
      price: "2424",
    },
    {
      id: "2",
      name: "bbb",
      price: "43524",
    },
  ];

  const columns = [
    {
      dataField: "id",
      text: "Product ID",
      headerStyle: (column, colIndex) => {
        return { width: "250px" };
      },
    },
    {
      dataField: "name",
      text: "Product Name",
      headerStyle: (column, colIndex) => {
        return { width: "250px" };
      },
    },
    {
      dataField: "price",
      text: "Product Price",
      headerStyle: (column, colIndex) => {
        return { width: "250px" };
      },
    },
  ];

  const expandRow = {
    renderer: (row) => (
      <div>
        <p>{`This Expand row belongs to rowKey ${row.id}`}</p>
        <p>
          You can render anything here, also you can add additional data on
          every row object
        </p>
        <p>
          expandRow.renderer callback will pass the origin row object to you
        </p>
      </div>
    ),
    showExpandColumn: true,
  };
  const { SearchBar } = Search;
  const afterSearch = (newResult) => {
    console.log(newResult);
  };

  return (
    <>
      <Navbar expand="lg" bg="light">
        <Navbar.Text>
          &nbsp; <strong>Dashboard : Expenses</strong>
        </Navbar.Text>
      </Navbar>
      <br />
      <Card style={{ width: "95%" }} className="ms-5">
        <Card.Header as="h4">
          <Col>
            <div className="dark">
              EXPENSE APPROVAL
              <Button
                variant="info"
                // style={{ marginRight: 5 }}
                id="filter"
                // onClick={showModal}
                className="float-end"
              >
                <i className="bi bi-funnel"></i> Filter
              </Button>
              <Button
                variant="success"
                style={{ marginRight: 5 }}
                id="Update"
                className="float-end"
              >
                <i className="bi bi-pencil-square"></i> Update Expense Entry
                Approvals
              </Button>
            </div>
          </Col>
        </Card.Header>
        <Card.Body>
          <ToolkitProvider
            keyField="name"
            data={data}
            columns={columns}
            search={{ afterSearch }}
          >
            {(props) => (
              <div>
                <SearchBar id="searchbar" {...props.searchProps} />
                <br />
                <BootstrapTable
                  //   ref={ n => this.node = n }
                  keyField="id"
                  data={data}
                  columns={columns}
                  //   defaultSorted={defaultSorted}
                  headerClasses="header-class"
                  //   filter={ filterFactory() }
                  pagination={paginationFactory()}
                  expandRow={expandRow}
                  // selectRow={{ mode: "checkbox", clickToSelect: true }}

                  {...props.baseProps}
                />
              </div>
            )}
          </ToolkitProvider>
        </Card.Body>
      </Card>
    </>
  );
};

export default Approvals;
