import React from "react";
import { Link, Redirect } from "react-router-dom";
import {
  Card,
  Dropdown,
  Button,
  Tab,
  Tabs,
  ListGroup,
  Row,
  Col,
  Table,
  Badge
} from "react-bootstrap";
import FileUpload from "../FileUpload";
import "./ProjectSummaryDashboard.css";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { getProjectById } from "../../actions/project";

const ProjectSummaryDashboard = (props) => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getProjectById(props.id))
      .then(() => {
        console.log("Project fetched successfully for summary");
      })
      .catch(() => {
        console.log("Unable to fetch project for summary");
      });
  }, [dispatch]);

  const { project:content } = useSelector((state) => state.projectById);
  if (content.length === 0) {
    return <Redirect to="/projects" />;
  }
  const project = content[0];
  // console.log("YAY?");
  // console.log(project);

  const statusType = {
    TO_DO: "To-Do",
    IN_PROGRESS: "In Progress",
    COMPLETED: "Completed",
    SUSPENDED: "Suspended",
  };

  // const project = props.content.filter((prj) => {
  //   return Number(props.id) === Number(prj.id);
  // })[0];

  console.log(project);

  return (
    <Row id="projectSummary">
      <Col sm={8}>
        <Card>
          <Card.Header as="h4">
            {project.client.fullName}
            <Dropdown className="float-end">
              <Dropdown.Toggle id="options-menu" variant="secondary">
                <i class="bi bi-gear-fill"></i> Options
              </Dropdown.Toggle>

              <Dropdown.Menu variant="dark">
                <Dropdown.Item as={Link} to="">
                  View
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="">
                  Edit
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="">
                  Delete
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
            <Card.Text className="float-end">&nbsp;</Card.Text>
            <Dropdown className="float-end">
              <Dropdown.Toggle id="options-menu" variant="success">
                + Add
              </Dropdown.Toggle>

              <Dropdown.Menu>
                <Dropdown.Item as={Link} to="">
                  View
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="">
                  Edit
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="">
                  Delete
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
            <Card.Text className="float-end">&nbsp;</Card.Text>
            <Button
              variant="danger"
              className="float-end"
              id="deleteProject"
              disabled={true}
            >
              Delete Selected
            </Button>
            {/* TODOS: */}
            {/* ROLE OF BUTTONS? */}
            <br />
            <h1>
              <font color="blue">{project.projectName}</font>
            </h1>
            <h6>
              <i class="bi bi-calendar3"></i> Due By:{" "}
              {project.endDate.substring(0, 10)}
              {/* TODOS: */}
              {/* Start date or end date? */}
              &nbsp; &nbsp;
              <i class="bi bi-card-list"></i> {statusType[project.status]}
            </h6>
          </Card.Header>
          <Card.Body>
            <Tabs defaultActiveKey="description">
              <Tab eventKey="description" title="Description">
                <br />
                {project.description}
              </Tab>

              <Tab eventKey="team" title="Team">
                <br />
                <strong>Team Members:</strong>
                <ul>
                  <li>
                    {project.projectManager.firstName +
                      " " +
                      project.projectManager.lastName}{" "}
                    <Badge pill bg="warning" text="dark">Project Manager</Badge>
                  </li>
                  {project.teamMembers
                    .filter((tm) => tm.id !== project.projectManager.id)
                    .map((tm) => {
                      return <li>{tm.firstName + " " + tm.lastName}</li>;
                    })}
                </ul>
              </Tab>

              {/* <Tab eventKey="attachment" title="Attachment">
                <br />
                <FileUpload />
              </Tab> */}
            </Tabs>
          </Card.Body>
        </Card>
      </Col>
      <Col>
        <Card>
          <Card.Header>
            <h3>
              <font color="blue">PROJECT SUMMARY</font>
            </h3>
          </Card.Header>
          <Card.Body>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Card.Text>
                  <strong>TOTAL HOURS</strong>
                </Card.Text>
                <Table borderless>
                  <tbody>
                    <tr>
                      <td>Billable Hours</td>
                      <td className="float-end">0.00</td>
                    </tr>
                    <tr>
                      <td>Non - Billable Hours</td>
                      <td className="float-end">0.00</td>
                    </tr>
                  </tbody>
                </Table>
              </ListGroup.Item>

              <ListGroup.Item>
                <Card.Text>
                  <strong>TOTAL EXPENSES</strong>
                </Card.Text>
                <Table borderless>
                  <tbody>
                    <tr>
                      <td>Billable Expenses</td>
                      <td className="float-end">0.00</td>
                    </tr>
                    <tr>
                      <td>Non - Billable Expenses</td>
                      <td className="float-end">0.00</td>
                    </tr>
                  </tbody>
                </Table>
              </ListGroup.Item>
            </ListGroup>
          </Card.Body>
        </Card>
      </Col>
    </Row>
  );
};

export default ProjectSummaryDashboard;
