import React,{useRef} from "react";
import ReactDOM from "react-dom";
import { useFormik } from "formik";
import Tabs from "react-bootstrap/Tabs";
import Tab from "react-bootstrap/Tab";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Modal from "react-bootstrap/Modal";
import { Formik } from "formik";
import * as yup from "yup";
import ClientService from "../../services/client.service";
import { useDispatch } from "react-redux";
import { Redirect, useHistory } from "react-router-dom";

export default function AddClient(props) {
  let history = useHistory();

  const dispatch = useDispatch();
  // const { clients: content } = useSelector((state) => state.client);
  var data = props.data;
  console.log("passed data");
  console.log(data);

  // ^(?:https?:\/\/|s?ftps?:\/\/)?(?!www | www\.)[A-Za-z0-9_-]+\.+[A-Za-z0-9.\/%&=\?_:;-]+$
  // {console.log(content[data])}
  // const schema = yup.object().shape({
  //   fullName: yup.string().required(),
  //   nickName: yup.string().required(),
  //   email: yup.string().email().required(),
  //   website: yup
  //     .string()
  //     .matches(
  //       /^(?:https?:\/\/|s?ftps?:\/\/)?(?!www | www\.)[A-Za-z0-9_-]+\.+[A-Za-z0-9.\/%&=\?_:;-]+$/
  //     )
  //     .required(),
  //   billDetails: yup.string(),
  //   address: yup.object().shape({
  //     addressLine1: yup.string().required(),
  //     addressLine2: yup.string(),
  //     city: yup.string().required(),
  //     state: yup.string().required(),
  //     country: yup.string().required(),
  //     zipcode: yup.string().required(),
  //     telephone1: yup.string().required(),
  //     telephone2: yup.string(),
  //     fax: yup.string(),
  //   }),
  // });
  // const schema = yup.object().shape({
  //   fullName: yup.string().required(),
  //   nickName: yup.string().required(),
  //   email: yup.string().email().required(),
  //   website: yup.string().url().required(),
  //   address: yup.object().shape()({
  //     addressLine1: yup.string().required(),
  //     addressLine2: yup.string().required(),
  //     city: yup.string().required(),
  //     state: yup.string().required(),
  //     country: yup.string().required(),
  //     zipcode: yup.string().required(),
  //     telephone1: yup.string().required(),
  //     telephone2: yup.string().required(),
  //     fax: yup.string().required()
  //   })
  // });

  const onSuccess = (values) => {
    // setLoading(true);
    console.log("entering into edit");
    console.log(values);
    dispatch(ClientService.addClient(values))
      .then(() => {
        history.push("/home");
      })
      .catch(() => {
        // setLoading(false);
      });
  };
  const handleSubmit = () => {
    if (formRef.current) {
      formRef.current.handleSubmit()
    }
  }
  const formRef = useRef();
  return (
    <Modal onHide={props.onHide} show={props.show} backdrop="static">
      <Modal.Header closeButton>
        <Modal.Title>{props.method} Client</Modal.Title>
      </Modal.Header>
      <Formik
        // validationSchema={schema}
        validateOnChange={true}
        onSubmit={onSuccess}
        initialValues={props.data}
        innerRef={formRef}
      >
        {({
          handleSubmit,
          handleChange,
          handleBlur,
          values,
          touched,
          isValid,
          errors,
        }) => (
          <Form noValidate onSubmit={handleSubmit}>
            <Modal.Body>
              <Row>
                <Form.Group as={Col} controlId="clientName">
                  <Form.Label>Client Name</Form.Label>
                  {/* {console.log("test")} */}

