import React,{useRef} from "react";
import ReactDOM from "react-dom";
import { useFormik } from "formik";
import Tabs from "react-bootstrap/Tabs";
import Tab from "react-bootstrap/Tab";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Modal from "react-bootstrap/Modal";
import { Formik } from "formik";
import * as yup from "yup";
import ClientService from "../../services/client.service";
import { useDispatch } from "react-redux";
import { Redirect, useHistory } from "react-router-dom";

export default function AddClient(props) {
  let history = useHistory();

  const dispatch = useDispatch();
  var data = props.data;
  console.log("passed data");
  console.log(data);

  const onSuccess = (values) => {
    // setLoading(true);
    console.log("entering into edit");
    console.log(values);
    dispatch(ClientService.addClient(values))
      .then(() => {
        history.push("/home");
      })
      .catch(() => {
      });
  };
  const handleSubmit = () => {
    if (formRef.current) {
      formRef.current.handleSubmit()
    }
  }
  const formRef = useRef();
  return (
    <Modal onHide={props.onHide} show={props.show} backdrop="static">
      <Modal.Header closeButton>
        <Modal.Title>{props.method} Client</Modal.Title>
      </Modal.Header>
      <Formik
        validateOnChange={true}
        onSubmit={onSuccess}
        initialValues={props.data}
        innerRef={formRef}
      >
        {({
          handleSubmit,
          handleChange,
          handleBlur,
          values,
          touched,
          isValid,
          errors,
        }) => (
          <Form noValidate onSubmit={handleSubmit}>
            <Modal.Body>
              <Row>
                <Form.Group as={Col} controlId="clientName">
                  <Form.Label>Client Name</Form.Label>
                  <Form.Control
                    name="fullName"
                    value={values.fullName}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    isInvalid={touched.fullName && errors.fullName}
                  />
                </Form.Group>
              </Row>
              <br />
              <Tabs defaultActiveKey="basic">
                <Tab eventKey="basic" title="Basic">
                  <br />
                  <Row>
                    <Form.Group as={Col} controlId="clientNick">
                      <Form.Label>Client Nick</Form.Label>
                      <Form.Control
                        name="nickName"
                        value={values.nickName}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.nickName && errors.nickName}
                      />
                    </Form.Group>
                    <Form.Group as={Col} controlId="email">
                      <Form.Label>Email address</Form.Label>
                      <Form.Control
                        name="email"
                        value={values.email}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.email && errors.email}
                      />
                    </Form.Group>
                  </Row>
                  <Row>
                    <Form.Group as={Col} controlId="website">
                      <Form.Label>Website</Form.Label>
                      <Form.Control
                        name="website"
                        value={values.website}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.website && errors.website}
                      />
                    </Form.Group>
                    <Form.Group as={Col} controlId="billingRate">
                      <Form.Label>Default Billing Rate</Form.Label>
                      <Form.Control
                        name="billDetails"
                        value={values.billDetails}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.billDetails && errors.billDetails}
                      />
                    </Form.Group>
                  </Row>
                </Tab>

                <Tab eventKey="details" title="Details">
                  <br />
                  <Row>
                    <Form.Group as={Col} controlId="address1">
                      <Form.Label>Address1</Form.Label>
                      <Form.Control
                        name="address.addressLine1"
                        value={values.address.addressLine1}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.addressLine1 && errors.addressLine1}
                      />
                    </Form.Group>
                    <Form.Group as={Col} controlId="address2">
                      <Form.Label>Address2</Form.Label>
                      <Form.Control
                        name="address.addressLine2"
                        value={values.address.addressLine2}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.addressLine2 && errors.addressLine2}
                      />
                    </Form.Group>
                  </Row>
                  <Row>
                    <Form.Group as={Col} controlId="country">
                      <Form.Label>Country</Form.Label>
                      <Form.Select
                      name="address.country"
                        value={values.address.country}  
                        onChange={handleChange}
                        onBlur={handleBlur}
                      >
                        <option value="US">US</option>
                        <option value="India">India</option>
                        <option value="Canada">Canada</option>
                      </Form.Select>
                    </Form.Group>
                    <Form.Group as={Col} controlId="city">
                      <Form.Label>City</Form.Label>
                      <Form.Control
                        name="address.city"
                        value={values.address.city}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.city && errors.city}
                      />
                    </Form.Group>
                  </Row>
                  <Row>
                    <Form.Group as={Col} controlId="state">
                      <Form.Label>State</Form.Label>
                      <Form.Control
                        name="address.state"
                        value={values.address.state}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.state && errors.state}
                      />
                    </Form.Group>
                    <Form.Group as={Col} controlId="zipcode">
                      <Form.Label>Zipcode</Form.Label>
                      <Form.Control
                        name="address.zipcode"
                        value={values.address.zipcode}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.zipcode && errors.zipcode}
                      />
                    </Form.Group>
                  </Row>
                  <Row>
                    <Form.Group as={Col} controlId="telephone1">
                      <Form.Label>Telephone1</Form.Label>
                      <Form.Control
                        name="address.telephone1"
                        value={values.address.telephone1}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.telephone1 && errors.telephone1}
                      />
                    </Form.Group>
                    <Form.Group as={Col} controlId="telephone2">
                      <Form.Label>Telephone2</Form.Label>
                      <Form.Control
                        name="address.telephone2"
                        value={values.address.telephone2}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.telephone2 && errors.telephone2}
                      />
                    </Form.Group>
                  </Row>
                  <Row>
                    <Form.Group as={Col} controlId="fax">
                      <Form.Label>Fax</Form.Label>
                      <Form.Control
                        name="address.fax"
                        value={values.address.fax}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.fax && errors.fax}
                      />
                    </Form.Group>
                  </Row>
                </Tab>
              </Tabs>
            </Modal.Body>
          </Form>
        )}
      </Formik>
      <Modal.Footer>
        <Button
          variant="primary"
          onClick={handleSubmit}
        >
          {props.method} Client
        </Button>
        <Button variant="secondary" onClick={props.onHide}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
}
