import React from "react";
import { useSelector } from "react-redux";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import { Navbar, Card, Badge } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { useEffect } from "react";
import { getProjects } from "../actions/project";

const Home = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getProjects())
      .then(() => {
        console.log("Projects fetched successfully");
      })
      .catch(() => {
        console.log("Unable to fetch projects");
      });
  }, [dispatch]);

  const SortCaret = (order, column) => {
    if (!order)
      return (
        <span class="float-end">
          <font color="grey">
            &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "asc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
          <font color="grey">
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "desc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;
          <font color="grey">
            <i class="bi bi-caret-up-fill"></i>
          </font>
          <i class="bi bi-caret-down-fill"></i>
        </span>
      );
    return null;
  };
  const { projects: content } = useSelector((state) => state.project);
  const projectData = content.map((prj) => {
    const ActiveStatus = prj.active ? "Active" : "Inactive";
    const BadgeType = ActiveStatus === "Active" ? "success" : "danger";
    const statusType = {
      TO_DO: "To-Do",
      IN_PROGRESS: "In Progress",
      COMPLETED: "Completed",
      SUSPENDED: "Suspended",
    };
    const soonDue = prj.soonDue ? <center><Badge bg="info">Soon Due</Badge></center> : "";
    const prjData = {
      projectActiveStatus: <center><Badge bg={BadgeType}>{ActiveStatus}</Badge></center>,
      projectDueStatus: soonDue,
      pName: prj.projectName,
      projectStatus: statusType[prj.status],
    };
    return prjData;
  });

  const columns = [
    {
      dataField: "projectActiveStatus",
      text: "",
    },
    {
      dataField: "projectDueStatus",
      text: "",
    },
    {
      dataField: "pName",
      text: "Project Name",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectStatus",
      text: "Status",
      sort: true,
      sortCaret: SortCaret,
    },
  ];

  const defaultSorted = [
    {
      dataField: "projectCode",
      order: "asc", //desc
    },
  ];

  const customTotal = (from, to, size) => (
    <span className="react-bootstrap-table-pagination-total">
      &nbsp; Showing {from} to {to} of {size} Results
    </span>
  );

  const options = {
    showTotal: true,
    paginationTotalRenderer: customTotal,
    disablePageTitle: true,
    sizePerPageList: [
      {
        text: "5",
        value: 5,
      },
      {
        text: "10",
        value: 10,
      },
      {
        text: "All",
        value: projectData.length,
      },
    ],
  };

  const { SearchBar } = Search;

  return (
    <>
      <Navbar expand="lg" bg="light">
        <Navbar.Text>
          &nbsp; <strong>Dashboard</strong>
        </Navbar.Text>
      </Navbar>
      <br />
      <Card className="ms-5" style={{ width: "95%" }}>
        <Card.Header as="h4">MY PROJECTS</Card.Header>
        <Card.Body>
          <ToolkitProvider
            keyField="projectCode"
            data={projectData}
            columns={columns}
            search
          >
            {(props) => (
              <div>
                <SearchBar
                  {...props.searchProps}
                  srText="Search Projects :"
                  placeholder="Enter text"
                />
                <br />
                <BootstrapTable
                  {...props.baseProps}
                  hover
                  defaultSorted={defaultSorted}
                  pagination={paginationFactory(options)}
                  noDataIndication="No data available in table"
                />
              </div>
            )}
          </ToolkitProvider>
        </Card.Body>
      </Card>
    </>
  );
};

export default Home;
