import {
  FETCH_PROJECT_BY_ID,
  FETCH_PROJECT_BY_ID_FAIL,
  FETCH_PROJECT_EXPENSES,
  FETCH_PROJECT_EXPENSES_FAIL,
} from "../actions/types";

const initialState = { project: [], projectExpense: [] };

export default function (state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case FETCH_PROJECT_BY_ID:
      const fetchedProject = [payload.project];
      return {
        ...state,
        project: fetchedProject,
        projectExpense,
      };
    case FETCH_PROJECT_BY_ID_FAIL:
      return {
        ...state,
        project: [],
        projectExpense,
      };
    case FETCH_PROJECT_EXPENSES:
      const fetchedProjectExpense = [payload.projectExpense];
      return {
        ...state,
        project,
        projectExpense: fetchedProjectExpense,
      };
    case FETCH_PROJECT_EXPENSES_FAIL:
      return {
        ...state,
        project,
        projectExpense: [],
      };
    default:
      return state;
  }
}
