import React from "react";
import {
  Navbar,
  Card,
  Button,
  Badge,
  Dropdown,
  Row,
  Col,
} from "react-bootstrap";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import ApprovalHeader from "./ApprovalHeader";

const Approvals = () => {

  const approveButton = (id) => {
    return (
      <Button variant="success" id={id}>
        Approve
      </Button>
    )
  }

  const rejectButton = (id) => {
    return (
      <Button variant="danger" id={id}>
        Reject
      </Button>
    )
  }

  const data = [
    {
      empName: "1",
      descpt: "aaa",
      amt: "2424",
      approve: <center>{approveButton(1)}</center>,
      reject: <center>{rejectButton(1)}</center>,
    },
  ];

  const columns = [
    {
      dataField: "empName",
      text: "Employee Name",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "descpt",
      text: "Description",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "amt",
      text: "Amount",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "approve",
      text: "",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
    {
      dataField: "reject",
      text: "",
      // headerStyle: (column, colIndex) => {
      //   return { width: "250px" };
      // },
    },
  ];

  const expandRow = {
    renderer: (row) => (
      <div>
        <p>{`This Expand row belongs to rowKey ${row.id}`}</p>
        <p>
          You can render anything here, also you can add additional data on
          every row object
        </p>
        <p>
          expandRow.renderer callback will pass the origin row object to you
        </p>
      </div>
    ),
    showExpandColumn: true,
  };
  const { SearchBar } = Search;

  return (
    <>
      <ApprovalHeader />
      <Card style={{ width: "95%" }} className="ms-5">
        <Card.Header as="h4">
          <Col>
            <div className="dark">EXPENSE APPROVAL</div>
          </Col>
        </Card.Header>
        <Card.Body>
          <ToolkitProvider
            keyField="empName"
            data={data}
            columns={columns}
            search
          >
            {(props) => (
              <div>
                <SearchBar id="searchbar" {...props.searchProps} 
                srText="Search Expenses :"
                placeholder="Enter text"/>
                <br />
                <BootstrapTable
                  keyField="approve"
                  {...props.baseProps}
                  hover
                  // data={data}
                  // columns={columns}
                  //   defaultSorted={defaultSorted}
                  // headerClasses="header-class"
                  //   filter={ filterFactory() }
                  pagination={paginationFactory()}
                  // expandRow={expandRow}
                  noDataIndication="No data available in the table for you"

                />
              </div>
            )}
          </ToolkitProvider>
        </Card.Body>
      </Card>
    </>
  );
};

export default Approvals;
