import React from "react";
import { useState } from "react";
import { useSelector } from "react-redux";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import { Card, Button, Container, Badge, Dropdown } from "react-bootstrap";
import AddProject from "./AddProject";
import { Link } from "react-router-dom";

const ProjectDashboard = () => {
  const [show, setShow] = useState(false);
  const [method, setMethod] = useState("Add");
  const initProj = {
    projectName: "",
    projectDescription: "",
    client: "",
    projectManager: "",
    status: "To-Do",
    startDate: "",
    endDate: "",
    duration: 0.0,
    cost: 0.0,
    teamMembers: [],
  };
  const [proj, setProj] = useState(initProj);
  const handleShow = () => setShow(true);
  const handleClose = () => {
    setShow(false);
    setProj(initProj);
    setMethod("Add");
  };

  const formDate = (date) => {
    let splitDate = date.split(" ");
    let newDate = splitDate[0];
    let splitDateList = newDate.split("-");
    splitDateList = splitDateList.reverse();
    const finalDate = splitDateList.join("-");
    return finalDate;
  };

  const handleSelect = (e) => {
    handleShow();

    const statusType = {
      TO_DO: "To-Do",
      IN_PROGRESS: "In Progress",
      COMPLETED: "Completed",
      SUSPENDED: "Suspended",
    };

    const index = content.findIndex(
      (o) => o.id.toString() === e.target.id.toString()
    );
    let dataGot = content[index];
    let toPass = {
      projectName: "",
      projectDescription: "",
      client: "",
      projectManager: "",
      status: "To-Do",
      startDate: "",
      endDate: "",
      duration: 0.0,
      cost: 0.0,
      teamMembers: [],
      projectID:-1,
    };
    toPass.projectName = dataGot.projectName;
    toPass.projectManager = dataGot.projectManager;
    toPass.projectDescription = dataGot.description;
    toPass.client = dataGot.client.id;
    toPass.projectManager = dataGot.projectManager.id;
    toPass.status = statusType[dataGot.status];
    toPass.startDate = formDate(dataGot.startDate);
    toPass.endDate = formDate(dataGot.endDate);
    toPass.teamMembers = dataGot.teamMembers;
    toPass.projectID = dataGot.id;
    console.log("entering ");
    console.log(dataGot);
    setProj(toPass);
    setMethod("Edit");
  };

  const SortCaret = (order, column) => {
    if (!order)
      return (
        <span class="float-end">
          <font color="grey">
            &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "asc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
          <font color="grey">
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "desc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;
          <font color="grey">
            <i class="bi bi-caret-up-fill"></i>
          </font>
          <i class="bi bi-caret-down-fill"></i>
        </span>
      );
    return null;
  };

  // TO CHANGE
  // const { projects:content } = useSelector((state) => state.project);
  let content = [
    {
      id: 1,
      client: {
        id: 1,
        fullName: "Client1",
        nickName: "cl1",
        email: "cl1@google.come",
        address: {
          addressLine1: "MNOP",
          addressLine2: "DEF",
          city: "ABC",
          state: "Haryana",
          country: "India",
          zipcode: "121004",
          telephone1: "12345",
          telephone2: "4567",
          fax: "56789",
        },
        website: "cl1.github.io",
        billDetails: "N/A",
      },
      description: "test",
      projectManager: {
        id: 1,
        email: "pmangla@adobe.com",
        firstName: "Puneet",
        lastName: "Mangla",
        password: "1234",
        role: "PRJ_MANAGER",
        isPasswordTemp: false,
        manager: null,
      },
      projectName: "prj15",
      startDate: "06-11-2021 11:10:09",
      endDate: "09-11-2021 11:10:09",
      status: "IN_PROGRESS",
      teamMembers: [
        {
          id: 3,
          email: "ashaw@adobe.com",
          firstName: "Abhisekh",
          lastName: "Shaw",
          password: "1234",
          role: "PRJ_MANAGER",
          isPasswordTemp: true,
          manager: null,
        },
        {
          id: 2,
          email: "ailani@adobe.com",
          firstName: "Bhanu",
          lastName: "Ailani",
          password: "1234",
          role: "USER",
          isPasswordTemp: true,
          manager: null,
        },
      ],
      attachments: [],
      active: false,
      soonDue: false,
    },
  ];
  let { projects } = useSelector((state) => state.project);

  const dropDown = (id, content) => {
    const idUrl = "/projectsummary/" + id;
    const editProj = content.filter((prj) => prj.id === id)[0];
    return (
      <Dropdown>
        <Dropdown.Toggle id="options-menu" variant="secondary">
          <i class="bi bi-gear-fill"></i> Options
        </Dropdown.Toggle>

        <Dropdown.Menu variant="dark">
          <Dropdown.Item as={Link} to={idUrl}>
            View Summary
          </Dropdown.Item>
          <Dropdown.Item onClick={handleSelect} id={id}>
            Edit
          </Dropdown.Item>
          <Dropdown.Item as={Link} to="">
            Delete
          </Dropdown.Item>
        </Dropdown.Menu>
      </Dropdown>
    );
  };
  const projectData = content.map((prj) => {
    const ActiveStatus = prj.active ? "Active" : "Inactive";
    const BadgeType = ActiveStatus === "Active" ? "success" : "danger";
    const statusType = {
      TO_DO: "To-Do",
      IN_PROGRESS: "In Progress",
      COMPLETED: "Completed",
      SUSPENDED: "Suspended",
    };
    const soonDue = prj.soonDue ? <Badge bg="info">Soon Due</Badge> : "";
    const prjData = {
      projectActiveStatus: <Badge bg={BadgeType}>{ActiveStatus}</Badge>,
      projectDueStatus: soonDue,
      projectCode: prj.id,
      pName: prj.projectName,
      cName: prj.client.fullName,
      projectStatus: statusType[prj.status],
      projectTeam:
        prj.projectManager.firstName + " " + prj.projectManager.lastName,
      projectOptions: dropDown(prj.id, content),
    };
    return prjData;
  });

  const columns = [
    {
      dataField: "projectActiveStatus",
      text: "",
      sort: true,
      sortCaret: SortCaret,
      headerStyle: (colum, colIndex) => {
        return { width: "80px", textAlign: "center" };
      },
    },
    {
      dataField: "projectDueStatus",
      text: "",
      sort: true,
      sortCaret: SortCaret,
      headerStyle: (colum, colIndex) => {
        return { width: "100px", textAlign: "center" };
      },
    },
    {
      dataField: "projectCode",
      text: "Code",
      sort: true,
      sortCaret: SortCaret,

      headerStyle: (colum, colIndex) => {
        return { width: "100px", textAlign: "center" };
      },
    },
    {
      dataField: "pName",
      text: "Project Name",
      sort: true,
