import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Redirect } from "react-router-dom";
import { Container, Card, Row, Toast, Col } from "react-bootstrap";
import { Formik } from "formik";
import * as yup from "yup";


const ChangePassword = () => {
  // console.log(props);
  const { isLoggedIn } = useSelector((state) => state.auth);
  if (!isLoggedIn) {
    return <Redirect to="/login" />;
  }
  const [showA, setShowA] = useState(false);
  const closeA = () => setShowA(false);
  const openA = () => setShowA(true);
  const [loading, setLoading] = useState(false);

  const schema = yup.object().shape({
    oldPwd: yup.string().string().required("Enter Old Password"),
    newPwd: yup
      .string()
      .notOneOf([yup.ref("oldPwd")], "New Password must not match Old Password")
      .required("Enter New Password"),
    confirmedPwd: yup
      .string()
      .oneOf([yup.ref("newPwd"), null], "Does not match with the New Password")
      .required(),
  });

  const dispatch = useDispatch();

  const onSuccess = (values) => {
    setLoading(true);
    console.log(values);

    // dispatch(login(values.email, values.password))
    //   .then((response) => {
    //     if(response.isPasswordTemp){
    //       history.push("/changepwd");
    //     }
    //     else{
    //       history.push("/home");
    //     }
    //   })
    //   .catch(() => {
    //     setLoading(false);
    //     openA();
    //   });
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col lg={4} md={6} />
        <Col lg={4} md={6}>
          {/* <p>{message}</p> */}
          <Card>
            <Card.Header className="text-center">
              <h4>Change Password</h4>
            </Card.Header>
            <Card.Body>
              <Formik
                validationSchema={schema}
                validateOnChange={true}
                onSubmit={onSuccess}
                initialValues={{
                  oldPwd: "",
                  newPwd: "",
                  confirmedPwd: "",
                }}
              >
                {({
                  handleSubmit,
                  handleChange,
                  handleBlur,
                  values,
                  touched,
                  isValid,
                  errors,
                }) => (
                  <Form noValidate onSubmit={handleSubmit}>
                    <Form.Group className="mb-3" size="lg" controlId="oldPwd">
                      <Form.Label>Temporary or Old Password</Form.Label>
                      <Form.Control
                        autoFocus
                        type="password"
                        name="oldPwd"
                        value={values.oldPwd}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.oldPwd && errors.oldPwd}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.oldPwd}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group size="lg" controlId="newPwd">
                      <Form.Label>New Password</Form.Label>
                      <Form.Control
                        type="password"
                        name="newPwd"
                        value={values.newPwd}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.newPwd && errors.newPwd}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.newPwd}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group size="lg" controlId="confirmedPwd">
                      <Form.Label>Confirm Password</Form.Label>
                      <Form.Control
                        type="password"
                        name="confirmedPwd"
                        value={values.confirmedPwd}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        isInvalid={touched.confirmedPwd && errors.confirmedPwd}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.confirmedPwd}
                      </Form.Control.Feedback>
                    </Form.Group>
                    <br />
                    <div className="d-grid">
                      <Button
                        variant="primary"
                        type="submit"
                        disabled={!isValid}
                      >
                        Submit
                      </Button>
                      <Button
                        variant="secondary"
                        type="reset"
                      >
                        Reset
                      </Button>
                    </div>
                  </Form>
                )}
              </Formik>
            </Card.Body>
          </Card>
        </Col>
        <Col lg={4} md={6} />
      </Row>
      <br />
      <Row className="justify-content-center">
        <Toast
          className="text-center"
          bg="danger"
          style={{ width: "18rem" }}
          show={showA}
          onClose={closeA}
          position="middle-center"
          delay={3000}
          autohide
        >
          <Toast.Header>
            <strong className="me-auto">ERROR!</strong>
          </Toast.Header>
          <Toast.Body>
            <h6>Invalid Login Credentials!</h6>
          </Toast.Body>
        </Toast>
      </Row>
    </Container>
  );
};

export default ChangePassword;
