import React from "react";
import { useState } from "react";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import { Card, Button, Container, Badge, Dropdown } from "react-bootstrap";
import AddProject from "./AddProject";
import ProjectSummaryHeader from "./ProjectSummaryHeader";
import { render } from "react-dom";

const ProjectDashboard = () => {
  const [show, setShow] = useState(false);
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  const SortCaret = (order, column) => {
    if (!order)
      return (
        <span class="float-end">
          <font color="grey">
            &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "asc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
          <font color="grey">
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "desc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;
          <font color="grey">
            <i class="bi bi-caret-up-fill"></i>
          </font>
          <i class="bi bi-caret-down-fill"></i>
        </span>
      );
    return null;
  };

  const dropDown = (
    <Dropdown>
      <Dropdown.Toggle id="options-menu" variant="secondary">
        <i class="bi bi-gear-fill"></i> Options
      </Dropdown.Toggle>

      <Dropdown.Menu variant="dark">
        <Dropdown.Item href="" active>
          Edit
        </Dropdown.Item>
        <Dropdown.Item href="">Delete</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );

  const columns = [
    {
      dataField: "projectActiveStatus",
      text: "",
      sort: true,
      sortCaret: SortCaret,
      headerStyle: (colum, colIndex) => {
        return { width: "80px", textAlign: "center" };
      },
    },
    {
      dataField: "projectDueStatus",
      text: "",
      sort: true,
      sortCaret: SortCaret,
      headerStyle: (colum, colIndex) => {
        return { width: "100px", textAlign: "center" };
      },
    },
    {
      dataField: "projectCode",
      text: "Code",
      sort: true,
      sortCaret: SortCaret,

      headerStyle: (colum, colIndex) => {
        return { width: "100px", textAlign: "center" };
      },
    },
    {
      dataField: "pName",
      text: "Project Name",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "cName",
      text: "Client Name",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectStatus",
      text: "Status",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectTeam",
      text: "Team",
    },
    {
      dataField: "projectOptions",
      text: "",

      headerStyle: (colum, colIndex) => {
        return { width: "140px", textAlign: "center" };
      },
    },
  ];

  const defaultSorted = [
    {
      dataField: "projectCode",
      order: "asc", //desc
    },
  ];

  const projectData = [
    {
      projectActiveStatus: <Badge bg="success">Active</Badge>,
      projectDueStatus: <Badge bg="info">Soon Due</Badge>,
      projectCode: 1,
      pName: <a href="/projectSummary">BI Software</a>,
      cName: "XYZ",
      projectStatus: "Started",
      projectTeam: "BP",
      projectOptions: dropDown,
    },
    {
      projectActiveStatus: <Badge bg="success">Active</Badge>,
      projectDueStatus: <Badge bg="info">Soon Due</Badge>,
      projectCode: 2,
      pName: "BL Software",
      cName: "ABC",
      projectStatus: "Started",
      projectTeam: "BP",
      projectOptions: dropDown,
    },
  ];

  const selectRow = {
    mode: 'checkbox',
    onSelect: (row, isSelect, rowIndex, e) => {
      if (row.pName === "BL Software") {
        render(
        <ProjectSummaryHeader val={true}/>
        )
      }
    }
  };

  const customTotal = (from, to, size) => (
    <span className="react-bootstrap-table-pagination-total">
      &nbsp; Showing {from} to {to} of {size} Results
    </span>
  );

  const options = {
    // paginationSize: 4,
    // pageStartIndex: 0,
    // alwaysShowAllBtns: true, // Always show next and previous button
    // withFirstAndLast: false, // Hide the going to First and Last page button
    // hideSizePerPage: true, // Hide the sizePerPage dropdown always
    // hidePageListOnlyOnePage: true, // Hide the pagination list when only one page
    // firstPageText: 'First',
    // prePageText: 'Back',
    // nextPageText: 'Next',
    // lastPageText: 'Last',
    // nextPageTitle: 'First page',
    // prePageTitle: 'Pre page',
    // firstPageTitle: 'Next page',
    // lastPageTitle: 'Last page',
    showTotal: true,
    paginationTotalRenderer: customTotal,
    disablePageTitle: true,
    sizePerPageList: [
      {
        text: "5",
        value: 5,
      },
      {
        text: "10",
        value: 10,
      },
      {
        text: "All",
        value: projectData.length,
      },
    ],
  };

  const { SearchBar } = Search;

  return (
    <Container>
      <Card style={{ width: "100%" }}>
        <Card.Header as="h4">
          PROJECTS
          <Button
            variant="success"
            className="float-end"
            id="addProject"
            onClick={handleShow}
          >
            + Add Project
          </Button>
          <Card.Text className="float-end">&nbsp;</Card.Text>
          <Button
            variant="danger"
            className="float-end"
            id="deleteProjects"
            disabled={true}
          >
            Delete Selected
          </Button>
        </Card.Header>
        <Card.Body>
          <ToolkitProvider
            keyField="projectCode"
            data={projectData}
            columns={columns}
            search
          >
            {(props) => (
              <div>
                <SearchBar
                  {...props.searchProps}
                  srText="Search Projects :"
                  placeholder="Enter text"
                />
                <br />
                <BootstrapTable
                  {...props.baseProps}
                  hover
                  selectRow={selectRow}//{{ mode: "checkbox" }}
                  defaultSorted={defaultSorted}
                  pagination={paginationFactory(options)}
                  noDataIndication="No data available in table"
                />
              </div>
            )}
          </ToolkitProvider>
        </Card.Body>
        <AddProject show={show} onHide={handleClose} />
      </Card>
    </Container>
  );
};

export default ProjectDashboard;
