import React from "react";
import { useState } from "react";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from 'react-bootstrap-table2-paginator';

import {
  Card,
  Button,
  Container,
  Badge,
  Form,
  Dropdown,
  Row,
  Col,
} from "react-bootstrap";
import AddProject from "./AddProject";

const ProjectDashboard = () => {
  const [show, setShow] = useState(false);
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  const SortCaret = (order, column) => {
    if (!order)
      return (
        <span class="float-end">
          <font color="grey">
            &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "asc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
          <font color="grey">
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "desc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;
          <font color="grey">
            <i class="bi bi-caret-up-fill"></i>
          </font>
          <i class="bi bi-caret-down-fill"></i>
        </span>
      );
    return null;
  };

  const dropDown = (
    <Dropdown>
      <Dropdown.Toggle id="options-menu" variant="secondary">
        <i class="bi bi-gear-fill"></i> Options
      </Dropdown.Toggle>

      <Dropdown.Menu variant="dark">
        <Dropdown.Item href="" active>
          Edit
        </Dropdown.Item>
        <Dropdown.Item href="">Delete</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );

  const columns = [
    {
      dataField: "projectActiveStatus",
      text: "",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectDueStatus",
      text: "",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectCode",
      text: "Code",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "pName",
      text: "Project Name",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "cName",
      text: "Client Name",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectStatus",
      text: "Status",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectTeam",
      text: "Team",
    },
    {
      dataField: "projectOptions",
      text: "",
    },
  ];

  const defaultSorted = [
    {
      dataField: "projectCode",
      order: "asc", //desc
    },
  ];

  const customTotal = (from, to, size) => (
    <span className="react-bootstrap-table-pagination-total">
      Showing { from } to { to } of { size } Results
    </span>
  );

  const options = {
    // paginationSize: 4,
    // pageStartIndex: 0,
    // alwaysShowAllBtns: true, // Always show next and previous button
    // withFirstAndLast: false, // Hide the going to First and Last page button
    // hideSizePerPage: true, // Hide the sizePerPage dropdown always
    // hidePageListOnlyOnePage: true, // Hide the pagination list when only one page
    // firstPageText: 'First',
    // prePageText: 'Back',
    // nextPageText: 'Next',
    // lastPageText: 'Last',
    // nextPageTitle: 'First page',
    // prePageTitle: 'Pre page',
    // firstPageTitle: 'Next page',
    // lastPageTitle: 'Last page',
    showTotal: true,
    paginationTotalRenderer: customTotal,
    disablePageTitle: true,
    sizePerPageList: [{
      text: '5', value: 5
    }, {
      text: '10', value: 10
    }, {
      text: 'All', value: products.length
    }] // A numeric array is also available. the purpose of above example is custom the text
  };

  const projectData = [
    {
      projectActiveStatus: <Badge bg="success">Active</Badge>,
      projectDueStatus: <Badge bg="info">Soon Due</Badge>,
      projectCode: 1,
      pName: "BI Software",
      cName: "XYZ",
      projectStatus: "Started",
      projectTeam: "BP",
      projectOptions: dropDown,
    },
    {
      projectActiveStatus: <Badge bg="success">Active</Badge>,
      projectDueStatus: <Badge bg="info">Soon Due</Badge>,
      projectCode: 2,
      pName: "BL Software",
      cName: "ABC",
      projectStatus: "Started",
      projectTeam: "BP",
      projectOptions: dropDown,
    },
  ];

  return (
    <Container>
      <Card style={{ width: "100%" }}>
        <Card.Header as="h4">
          PROJECTS
          <Button
            variant="success"
            className="float-end"
            id="addProject"
            onClick={handleShow}
          >
            + Add Project
          </Button>
          <Card.Text className="float-end">&nbsp;</Card.Text>
          <Button
            variant="danger"
            className="float-end"
            id="deleteProjects"
            disabled={true}
          >
            Delete Selected
          </Button>
        </Card.Header>
        <Card.Body>
          <Card.Text>
            Show &nbsp;
            <Dropdown className="d-inline">
              <Dropdown.Toggle variant="outline-secondary">5</Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item href="#">5</Dropdown.Item>
                <Dropdown.Item href="#">10</Dropdown.Item>
                <Dropdown.Item href="#">20</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
            &nbsp; entries
            <Form className="float-end">
              <Form.Group
                as={Row}
                className="mb-3"
                controlId="formHorizontalSearchm"
              >
                <Form.Label column sm={4}>
                  &nbsp; &nbsp; &nbsp; Search :
                </Form.Label>
                <Col sm={8}>
                  <Form.Control type="text" />
                </Col>
              </Form.Group>
            </Form>
          </Card.Text>
          <BootstrapTable
            keyField="projectCode"
            data={projectData}
            columns={columns}
            hover
            selectRow={{ mode: "checkbox" }}
            defaultSorted={defaultSorted}
            pagination={ paginationFactory() }
            noDataIndication="No data available in table"
          />
          {/* Showing 1 to 1 of 1 entries */}
        </Card.Body>
        <AddProject show={show} onHide={handleClose} />
      </Card>
    </Container>
  );
};

export default ProjectDashboard;
