import React from "react";
import { useState } from "react";
import { useSelector } from "react-redux";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import { Card, Button, Container, Badge, Dropdown } from "react-bootstrap";
import AddProject from "./AddProject";
import { Link } from "react-router-dom";
import EditProject from "./EditProject";

const ProjectDashboard = () => {
  const [show, setShow] = useState(false);
  // const [edit, setEdit] = useState(false);
  const [editContent, setEditContent] = useState([]);
  const [method, setMethod] = useState("Add");
  const initProj = {
    projectName: "",
    projectDescription: "",
    client: "",
    projectManager: "",
    status: "To-Do",
    startDate: "",
    endDate: "",
    duration: 0.0,
    cost: 0.0,
  };
  const [proj, setProj] = useState(initProj);
  const handleShow = () => setShow(true);
  const handleClose = () => {
    setShow(false);
    setProj(initProj);
    setMethod("Add");
  }

  const formDate = (date) => {
    let splitDate = date.split(" ");
    let newDate = splitDate[0];
    let splitDateList = newDate.split("-");
    splitDateList = splitDateList.reverse();
    const finalDate = splitDateList.join("/");
    return finalDate;
  };

  const handleSelect = (e) => {
    handleShow();

    const statusType = {
      TO_DO: "To-Do",
      IN_PROGRESS: "In Progress",
      COMPLETED: "Completed",
      SUSPENDED: "Suspended",
    };

    const index = content.findIndex(
      (o) => o.id.toString() === e.target.id.toString()
    );
    let dataGot = content[index];
    let toPass = {
      projectName: "",
      projectDescription: "",
      client: "",
      projectManager: "",
      status: "To-Do",
      startDate: "",
      endDate: "",
      duration: 0.0,
      cost: 0.0,
    };
    toPass.projectName = dataGot.projectName;
    toPass.projectManager = dataGot.projectManager;
    toPass.projectDescription = dataGot.description;
    toPass.client = dataGot.client.id;
    toPass.projectManager = dataGot.projectManager.id;
    toPass.status = statusType[dataGot.status];
    toPass.startDate = formDate(dataGot.startDate);
    toPass.endDate = formDate(dataGot.endDate);
    console.log("entering ");
    // console.log(content[index]);
    setProj(toPass);
    setMethod("Edit");
  };
  // const handleShowEdit = (editProj) => () => {setEditContent([editProj]);console.log(editContent);setEdit(true);}
  // const handleCloseEdit = () => {
  //   setEdit(false);
  // }

  const SortCaret = (order, column) => {
    if (!order)
      return (
        <span class="float-end">
          <font color="grey">
            &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "asc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;<i class="bi bi-caret-up-fill"></i>
          <font color="grey">
            <i class="bi bi-caret-down-fill"></i>
          </font>
        </span>
      );
    else if (order === "desc")
      return (
        <span class="float-end">
          &nbsp;&nbsp;
          <font color="grey">
            <i class="bi bi-caret-up-fill"></i>
          </font>
          <i class="bi bi-caret-down-fill"></i>
        </span>
      );
    return null;
  };

  const { projects:content } = useSelector((state) => state.project);

  const dropDown = (id,content) => {
    const idUrl = "/projectsummary/" + id;
    const editProj = content.filter((prj) => prj.id === id)[0];
    return (
      <Dropdown>
        <Dropdown.Toggle id="options-menu" variant="secondary">
          <i class="bi bi-gear-fill"></i> Options
        </Dropdown.Toggle>

        <Dropdown.Menu variant="dark">
          <Dropdown.Item as={Link} to={idUrl}>View Summary</Dropdown.Item>
          {/* <Dropdown.Item as={Link} to="">Edit</Dropdown.Item> */}
          <Dropdown.Item onClick={handleSelect} id={id}>Edit</Dropdown.Item>
          <Dropdown.Item as={Link} to="">Delete</Dropdown.Item>
        </Dropdown.Menu>
      </Dropdown>
    );
  };
  const projectData = content.map((prj) => {
    const ActiveStatus = prj.active ? "Active" : "Inactive";
    const BadgeType = ActiveStatus === "Active" ? "success" : "danger";
    const statusType = { "TO_DO":"To-Do","IN_PROGRESS": "In Progress", "COMPLETED": "Completed", "SUSPENDED": "Suspended" };
    const soonDue = prj.soonDue?<Badge bg="info">Soon Due</Badge>:""
    const prjData = {
      projectActiveStatus: <Badge bg={BadgeType}>{ActiveStatus}</Badge>,
      projectDueStatus: soonDue,
      projectCode: prj.id,
      pName: prj.projectName,
      cName: prj.client.fullName,
      projectStatus: statusType[prj.status],
      projectTeam:
        prj.projectManager.firstName + " " + prj.projectManager.lastName,
      projectOptions: dropDown(prj.id, content),
    };
    return prjData;
  });

  const columns = [
    {
      dataField: "projectActiveStatus",
      text: "",
      sort: true,
      sortCaret: SortCaret,
      headerStyle: (colum, colIndex) => {
        return { width: "80px", textAlign: "center" };
      },
    },
    {
      dataField: "projectDueStatus",
      text: "",
      sort: true,
      sortCaret: SortCaret,
      headerStyle: (colum, colIndex) => {
        return { width: "100px", textAlign: "center" };
      },
    },
    {
      dataField: "projectCode",
      text: "Code",
      sort: true,
      sortCaret: SortCaret,

      headerStyle: (colum, colIndex) => {
        return { width: "100px", textAlign: "center" };
      },
    },
    {
      dataField: "pName",
      text: "Project Name",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "cName",
      text: "Client Name",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectStatus",
      text: "Status",
      sort: true,
      sortCaret: SortCaret,
    },
    {
      dataField: "projectTeam",
      text: "Team",
    },
    {
      dataField: "projectOptions",
      text: "",

      headerStyle: (colum, colIndex) => {
        return { width: "140px", textAlign: "center" };
      },
    },
  ];

  const defaultSorted = [
    {
      dataField: "projectCode",
      order: "asc", //desc
    },
  ];

  const customTotal = (from, to, size) => (
    <span className="react-bootstrap-table-pagination-total">
      &nbsp; Showing {from} to {to} of {size} Results
    </span>
  );

  const options = {
    // paginationSize: 4,
    // pageStartIndex: 0,
    // alwaysShowAllBtns: true, // Always show next and previous button
    // withFirstAndLast: false, // Hide the going to First and Last page button
    // hideSizePerPage: true, // Hide the sizePerPage dropdown always
    // hidePageListOnlyOnePage: true, // Hide the pagination list when only one page
    // firstPageText: 'First',
    // prePageText: 'Back',
    // nextPageText: 'Next',
    // lastPageText: 'Last',
    // nextPageTitle: 'First page',
    // prePageTitle: 'Pre page',
    // firstPageTitle: 'Next page',
    // lastPageTitle: 'Last page',
    showTotal: true,
    paginationTotalRenderer: customTotal,
    disablePageTitle: true,
    sizePerPageList: [
      {
        text: "5",
        value: 5,
      },
      {
        text: "10",
        value: 10,
      },
      {
        text: "All",
        value: projectData.length,
      },
    ],
  };

  const { SearchBar } = Search;

  return (
    <Container>
      <Card style={{ width: "100%" }}>
        <Card.Header as="h4">
          PROJECTS
          <Button
            variant="success"
            className="float-end"
            id="addProject"
            onClick={handleShow}
          >
            + Add Project
          </Button>
          <Card.Text className="float-end">&nbsp;</Card.Text>
          <Button
            variant="danger"
            className="float-end"
            id="deleteProjects"
            disabled={true}
          >
            Delete Selected
          </Button>
        </Card.Header>
        <Card.Body>
          <ToolkitProvider
            keyField="projectCode"
            data={projectData}
            columns={columns}
            search
          >
            {(props) => (
              <div>
                <SearchBar
                  {...props.searchProps}
                  srText="Search Projects :"
                  placeholder="Enter text"
                />
                <br />
                <BootstrapTable
                  {...props.baseProps}
                  hover
                  selectRow={{ mode: "checkbox" }}
                  defaultSorted={defaultSorted}
                  pagination={paginationFactory(options)}
                  noDataIndication="No data available in table"
                />
              </div>
            )}
          </ToolkitProvider>
        </Card.Body>
        <AddProject show={show} onHide={handleClose}
            data={proj}
            method={method}/>
        {/* <EditProject show={edit} onHide={handleCloseEdit} content={editContent}/> */}
      </Card>
    </Container>
  );
};

